import React, { useState } from 'react';
import '../styles/Calculator.css';

const Calculator: React.FC = () => {
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('');
  const [complexOutput, setComplexOutput] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/calculate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ expression: input }),
      });
      const data = await response.json();
      setOutput(data.result);
      setComplexOutput(data.complex_result);
    } catch (error) {
      console.error('Calculation error:', error);
    }
  };

  return (
    <div className="calculator">
      <h1>Complex Calculator</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Enter a simple expression (e.g. 1+1)"
        />
        <button type="submit">Calculate</button>
      </form>
      {output && (
        <div className="result">
          <h2>Result:</h2>
          <p>{output}</p>
        </div>
      )}
      {complexOutput && (
        <div className="complex-result">
          <h2>Complex Calculation:</h2>
          <p>{complexOutput}</p>
        </div>
      )}
    </div>
  );
};

export default Calculator;
