import React, { useState } from 'react';
import '../styles/Calculator.css';

const Calculator = () => {
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');
  const [complexExpression, setComplexExpression] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setError('');
    setResult('');
    setComplexExpression('');

    try {
      const response = await fetch('http://localhost:5000/api/calculate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ expression: input }),
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Server responded with an error:', response.status, errorText);
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      if (data.error) {
        setError(data.error);
      } else {
        setResult(data.result);
        setComplexExpression(data.complex_expression);
      }
    } catch (error) {
      console.error('Calculation error:', error);
      setError('計算中にエラーが発生しました。サーバーの応答を確認してください。');
    }
  };

  return (
    <div className="calculator">
      <h1>複雑計算機</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="式を入力 (例: 1+1)"
        />
        <button type="submit">計算</button>
      </form>
      {error && <div className="error">{error}</div>}
      {complexExpression && (
        <div className="complex-expression">
          複雑化した式: {complexExpression}
        </div>
      )}
      {result && <div className="result">結果: {result}</div>}
    </div>
  );
};

export default Calculator;
